mapper.update:
update keyword
set content = #{content,jdbcType=VARCHAR},
  pid = #{pid,jdbcType=INTEGER},
  meeting = #{meeting,jdbcType=VARCHAR},
  "year" = #{year,jdbcType=VARCHAR}
where kpid = #{kpid,jdbcType=INTEGER}

mapper.select:
select kpid, content, pid, meeting, "year"
from keyword
where kpid = #{kpid,jdbcType=INTEGER}
